import React,{Component} from "react";
import {Bar, Doughnut, Polar, Radar, HorizontalBar} from 'react-chartjs-2';

class SkillsChart extends Component
{

    constructor(props)
    {
        super(props);
        this.state = 
        {
            chartData: props.chartData
        }
    }
    render()
    {
        return(
            <div>
                {/* <Polar
                    data = {this.state.chartData}
                    options={{
                        scales: {
                          yAxes: [
                            {
                                gridLines: {
                                  color: '#aaa',
                                  borderDash: [1, 3],
                                },
                                display: false, // this will hide vertical lines
                              },
                          ],
                          xAxes: [
                            {
                              gridLines: {
                                color: '#aaa',
                                borderDash: [1, 3],
                              },
                              display: false, // this will hide vertical lines
                            },
                          ],
                        },
                      }}
                /> */}
                <HorizontalBar data={chartData} />
                <h2 style = {{"position":"absolute", "left":"20%"}}>
                Programming Languages</h2>
            </div>
            )
    }
}
            
export default SkillsChart
