import React from "react";
import Flippy, { FrontSide, BackSide } from "react-flippy";
function FlipCard(props) {
  return (
    <Flippy
      flipOnHover={false}
      flipOnClick={true}
      flipDirection="horizontal"
      style={{
        width: props.width,
        height: props.height,
        display: "inline-block",
      }}
    >
      <FrontSide style = {{backgroundImage: "url(../../Images/pLangsBg.jfif)"}}>
        {props.Front}
      </FrontSide>
      <BackSide style={{ backgroundColor: "transparent", color: "#000000"}}>
        {props.Back}
      </BackSide>
    </Flippy>
  );
}

export default FlipCard;
