import React from "react";
import Flippy, { FrontSide, BackSide } from "react-flippy";
function FlipCard(props) {
  return (
    <Flippy
      flipOnHover={false}
      flipOnClick={true}
      flipDirection="horizontal"
      style={{
        width: "400px",
        height: "400px",
        display: "inline-block",
      }}
    >
      <FrontSide style={{ backgroundColor: "#0049B7" }}>
        {props.Front}
      </FrontSide>
      <BackSide style={{ backgroundColor: "#00DDDD", color: "#FFFFFF", height:'100vg' }}>
        {props.Back}
      </BackSide>
    </Flippy>
  );
}

export default FlipCard;
