import React,{Component} from "react";
import { ThemeConsumer } from "react-bootstrap/esm/ThemeProvider";
import {Bar, Doughnut, Polar, Radar, HorizontalBar} from 'react-chartjs-2';
import FlipCard from '../Components/FlipCard'
import Road from '../Components/Road'
import Header from "../Components/Header"
class SkillsChart extends Component
{

    constructor(props)
    {
        super(props);
        this.state = 
        {
            plChartData: props.chartDataPL,
            mlChartData: props.chartDataML,
            webDevChartData: props.webDevChartData,
            devOpsChartData: props.devOpsChartData,
            softSkillsChartData: props.softSkillsChartData,
            clickMessage: 'Click to Flip!'
        }
    }
    render()
    {
        return(
          <div>
              <Header mainHeader={'My SKills yo'}/>
              <div style = {{display: 'flex', flexDirection: 'row'}}>                
                  <FlipCard
                    width = {"25em"}
                    height = {"15em"}
                    Front=
                    {
                      <div>
                        <h1 className = 'progLang-hF'>
                        {this.state.plChartData.frontTitle}
                        </h1>
                        <p style= {{position: 'fixed', bottom: -16, right:0, color:'yellow'}}>
                          {this.state.clickMessage}
                        </p>
                      </div>
                    }
                    Back = {
                      <div>
                        <Doughnut data={this.state.plChartData}
                          options= {this.state.plChartData.options}
                        />
                      </div>
                        }
                  >
                  </FlipCard>
                  <Road 
                  width={'40em'}
                  height= {'10em'}
                  StylingInfo = {'horizontal-road1'}/>
                  <div style = {{display: 'flex', flexDirection: 'column', marginTop:'1.6%'}}>
                    <Road 
                      width={'10em'}
                      height={'10em'}
                      StylingInfo = {'horizontal-road2'}
                    />
                      <Road 
                     width={'10em'}
                     height={'5em'}
                     StylingInfo = {'vertical-road'}
                       />
                    <FlipCard
                    width = {"25em"}
                    height = {"24em"}
                    Front=
                    {
                      <div>
                        <h1 className = 'progLang-hF'>
                        {this.state.plChartData.frontTitle}
                        </h1>
                        <p style= {{position: 'fixed', bottom: -16, right:0, color:'yellow'}}>
                          {this.state.clickMessage}
                        </p>
                      </div>
                    }
                    Back = {
                      <div>
                        <Doughnut data={this.state.plChartData}
                          options= {this.state.plChartData.options}
                        />
                      </div>
                        }
                  >
                  </FlipCard>
                  <Road 
                     width={'10em'}
                     height={'5em'}
                     StylingInfo = {'vertical-road'}
                       />
                  <Road 
                     width={'10em'}
                     height={'10em'}
                     StylingInfo = {'vertical-road1'}
                     paddingRight = {'60%'}
                  />
                  <div style = {{display: 'flex', flexDirection:'row-reverse',
                                 position:'relative', direction:'rtl'}}>
                    <Road 
                      width={'20em'}
                      height= {'10em'}
                      StylingInfo={'horizontal-road1'}
                      // top={'-10.51em'}
                      // right = {'19.4em'}
                      top={'-100%'}
                      right = {'77.6%'}

                    />
                    {/* <Road 
                      width={'10em'}
                      height= {'10em'}
                      StylingInfo={'horizontal-road1'}
                    />
                    <Road 
                      width={'10em'}
                      height= {'10em'}
                      StylingInfo={'horizontal-road1'}
                    /> */}
                  </div>
                  </div>
              </div>
              
              
                 
          </div>
              
            )
    }
}
            
export default SkillsChart
