import React, { Component } from "react";

class Slider extends Component {
  constructor(props) {
    super();
    this.state = {
      content: [
        {
          id: 0,
          mainHeader: "Project1",
          subHeader: "News Classifier using roBERTa",
          imgSrc: "bert",
          link: "",
          selected: false,
        },
        {
          id: 1,
          mainHeader: "Project2",
          subHeader: "Docker for Starexec",
          imgSrc: "starexec",
          link: "",
          selected: false,
        },
        {
          id: 2,
          mainHeader: "Project3",
          subHeader: "",
          imgSrc: "blah",
          link: "",
          selected: false,
        },
      ],
    };
  }

  handleClick = (id, slide) => {
    let items = [...this.state.content];
    items[id].selected = items[id].selected ? false : true;
    items.forEach((item) => {
      if (item.id !== id) {
        item.selected = false;
      }
    });
  };

  makeItems = (content) => {
    return content.map((item) => {
      return;
      <Slide
        item={item}
        onClick={(e) => this.handleClick(item, id, e)}
        key={item.id}
      />;
    });
  };

  render() {
    return <p>This is the slider</p>;
  }
}

export default Slider;
