import React,{Component} from "react";
import { ThemeConsumer } from "react-bootstrap/esm/ThemeProvider";
import {Bar, Doughnut, Polar, Radar, HorizontalBar} from 'react-chartjs-2';
import FlipCard from '../Components/FlipCard'
import Road from '../Components/Road'
class SkillsChart extends Component
{

    constructor(props)
    {
        super(props);
        this.state = 
        {
            plChartData: props.chartDataPL,
            mlChartData: props.chartDataML,
            webDevChartData: props.webDevChartData,
            devOpsChartData: props.devOpsChartData,
            softSkillsChartData: props.softSkillsChartData,
            clickMessage: 'Click to Flip!'
        }
    }
    render()
    {
        return(
            <div style = {{display: 'flex', flexDirection: 'row'}}>
                {/* <Polar
                    data = {this.state.chartData}
                    options={{
                        scales: {
                          yAxes: [
                            {
                                gridLines: {
                                  color: '#aaa',
                                  borderDash: [1, 3],
                                },
                                display: false, // this will hide vertical lines
                              },
                          ],
                          xAxes: [
                            {
                              gridLines: {
                                color: '#aaa',
                                borderDash: [1, 3],
                              },
                              display: false, // this will hide vertical lines
                            },
                          ],
                        },
                      }}
                /> */}
                <FlipCard
                  Front=
                  {
                    <div>
                      <h1 className = 'progLang-hF'>
                      {this.state.plChartData.frontTitle}
                      </h1>
                      <p style= {{position: 'fixed', bottom: -16, right:0, color:'yellow'}}>
                        {this.state.clickMessage}
                      </p>
                    </div>
                  }
                  Back = {
                    <div>
                      <Doughnut data={this.state.plChartData}
                        options= {this.state.plChartData.options}
                      />
                    {/* <h2 style = {{"position":"absolute", "left":"20%"}}>
                     Programming Languages</h2> */}
                    </div>
                      }
                >
                </FlipCard>
                <Road />
                <Road />
            </div>
            )
    }
}
            
export default SkillsChart
