import React, { Component } from "react";
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import Container from "react-bootstrap/Container";
import Navbar from "react-bootstrap/Navbar";
import Nav from "react-bootstrap/Nav";
import Footer from "./Components/Footer.js";
import HomePage from "./MyPages/HomePage";
import AboutPage from "./MyPages/AboutPage";
import ContactPage from "./MyPages/ContactPage";
class App extends Component {
  constructor(props) {
    super();
    {
      this.state = {
        title: "ABDULLAH ZAHID",
        headerLinks: [
          { title: "Home", path: "/" },
          { title: "About", path: "/" },
          { title: "Contact", path: "/" },
        ],

        home: {
          mainHeader: "Nothing == Impossible",
          subHeader: "Solving Problems through Programming",
          subText: "Check it out below!",
        },
        about: {
          mainHeader: "Who Am I?",
        },
        contact: {
          mainHeader: "Connect with me",
        },
      };
    }
  }
  render() {
    return (
      <Router>
        <Container className="p-0" fluid={true}>
          <Navbar className="border-bottom" bg="transparent" expand="lg">
            <Navbar.Brand style={{ color: "#E7DFDD" }}>
              Abdullah Zahid
            </Navbar.Brand>
            <Navbar.Toggle className="border-0" aria-controls="navbar-toggle" />
            <Navbar.Collapse id="navbar-toggle" style={{ color: "#E7DFDD" }}>
              <Nav className="ml-auto">
                <Link style={{ color: "#E7DFDD" }} className="nav-link" to="/">
                  Home
                </Link>
                <Link
                  style={{ color: "#E7DFDD" }}
                  className="nav-link"
                  to="/about"
                >
                  About
                </Link>
                <Link
                  style={{ color: "#E7DFDD" }}
                  className="nav-link"
                  to="/contact"
                >
                  Contact
                </Link>
              </Nav>
            </Navbar.Collapse>
          </Navbar>

          <Route
            path="/"
            exact
            render={() => (
              <HomePage
                mainHeader={this.state.home.mainHeader}
                subHeader={this.state.home.subHeader}
                subText={this.state.home.subText}
              />
            )}
          />
          <Route
            path="/about"
            render={() => (
              <AboutPage mainHeader={this.state.about.mainHeader} />
            )}
          />
          <Route
            path="/contact"
            render={() => (
              <ContactPage mainHeader={this.state.contact.mainHeader} />
            )}
          />

          <Footer />
        </Container>
      </Router>
    );
  }
}
export default App;
