import React from "react";
import Flippy, { FrontSide, BackSide } from "react-flippy";
function FlipCard() {
  return (
    <Flippy
      flipOnHover={true}
      flipOnClick={false}
      flipDirection="horizontal"
      style={{
        width: "300px",
        height: "300px",
        display: "inline-block",
      }}
    >
      <FrontSide style={{ backgroundColor: "#0049B7" }}>
        Blah Blah
      </FrontSide>
      <BackSide style={{ backgroundColor: "#00DDDD", color: "#FFFFFF" }}>
        <h2>....</h2>
      </BackSide>
    </Flippy>
  );
}

export default FlipCard;
